e ("'", "", $used);	return $used;}class Website {	var $worth;	var $domain;	var $keyword;	var $pagerank;	var $title;	var $description;	var $years;	var $days;	var $age;	var $daysCount;}function endings($n, $form1, $form2, $form5) {$n = abs($n) % 100;$n1 = $n % 10;if ($n > 10 && $n < 20) return $form5;if ($n1 > 1 && $n1 < 5) return $form2;if ($n1 == 1) return $form1;return $form5;}function worthSort($s1, $s2) {	if ($s1->worth < $s2->worth) return 1;	elseif($s1->worth > $s2->worth) return -1;	else return 0;}function getSearchInit($key, $info, $test, $age) {	$whatToSelect = "domain, keyword, pagerank, title, description, age, worth";	if ($test) {		$sql = "SELECT $whatToSelect FROM worth WHERE domain like '%$key%' LIMIT 100";	} else {		$sql = "SELECT $whatToSelect FROM worth WHERE keyword like '%$key%' LIMIT 100";	}	$rs_result = mysql_query($sql);	$arr = array();	while ($row = mysql_fetch_assoc($rs_result)) {		$website = new Website;		$website->domain = $row[domain];		$website->keyword = $row[keyword];		$website->worth = $row[worth];		$website->pagerank = $row[pagerank];		$website->title = $row[title];		$website->description = $row[description];		if ($row[age] != 'Unknown') {			list($website->years, $bebebe1, $website->days, $bebebe2) = explode(" ", $row[age]);			$website->age = $row[age];			$website->daysCount = ($website->years * 365) + $website->days;		}		$arr[] = $website;	}	uasort($arr,"worthSort");	$out = "<br>".$info."<br><br><table style=\"float:left;\">";	$count_ = 1;	foreach($arr as $site) {		if ($count_ <= 5) {		$domainKeys = $site->keyword;		$testCommas = array_map('trim',explode(",",$domainKeys));		$testSpaces = array_map('trim',explode(" ",$domainKeys));		$splitted = (count($testCommas) <= 1 ? $testSpaces : $testCommas);		if ($site->years) {			$siteAge = $site->years." ".endings($site->years, $ageYears1, $ageYears2, $ageYears5