<?include 'config.php';$lang = $_GET['lang'];if ($lang) {	include($lang.".php");} else {	$lang = "en";	include("en.php");}function endings($n, $form1, $form2, $form5) {	$n = abs($n) % 100;	$n1 = $n % 10;	if ($n > 10 && $n < 20) return $form5;	if ($n1 > 1 && $n1 < 5) return $form2;	if ($n1 == 1) return $form1;	return $form5;}if (!isset($_GET['domain'])) {	return;} else {	$domain = $_GET['domain'];	$out = "<table>";	$sql1 = "SELECT COUNT(*) AS num FROM comments WHERE domain = '$domain'";	$rs_result1 = mysql_query($sql1);	while ($row = mysql_fetch_assoc($rs_result1)) {		$count = $row['num'];	}	$sql = "SELECT * FROM comments WHERE domain = '$domain' ORDER BY id DESC";	$rs_result = mysql_query($sql);	while ($row = mysql_fetch_assoc($rs_result)) {		$count++;		$date=$row[time];		$commentTime=floor((mktime()-strtotime($date))/86400);		if ($commentTime > 0) {			if ($commentTime == 1) {				$cTime = "<span style=\"font-family:georgia,serif;font-size:11pt;\"><i>".$cYesterday."</i></span>";			} else {				$cTime = "<span style=\"font-family:georgia,serif;font-size:11pt;\"><i>".$commentTime." ".endings($commentTime, $cDay1, $cDay2, $cDay5)." ".$cAgo."</i></span>";			}		} else {			$cTime = "<span style=\"font-family:georgia,serif;font-size:11pt;\"><i>".$cToday."</i></span>";		}		if ($row['name']) {			$cName = "<span style=\"font-family:georgia,serif;font-size:13pt;\"><b>".$row['name']."</b></span>";		} else {			$cName = "<span style=\"font-family:georgia,serif;font-size:13pt;\"><b>".$cAnonymous."</b></span>";		}		if ($row['site']) {			$cSite = $cFrom_." <a target=\"_blank\" href=\"http://sitecostcalculator.com/www.".$row['site']."\"><span style=\"font-family:georgia,serif;font-size:11pt;\">".$row['site']."</span></a> ";		} else {		}		$out .= "<translate><td>".$cName." ".$cSite." ".$cSaid." ".$cTime.":<br><span style=\"font-family:georgia,serif;font-size:12pt;\">".$row['comment']."</span></td></translate><translate><td>&nbsp;</td></translate>";	}	$out .= "</table>";	if ($count <= 0) {		$out = "<center><span style=\"font-family:georgia,serif;font-size:13pt;\"><b>".$cNo."</b></span></center>";	}}mysql_close($con);echo $out;?>